import './index.css';

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: true },
  { id: 2, description: "Socks", quantity: 12, packed: false },
  { id: 3, description: "Charger", quantity: 12, packed: false },
  { id: 4, description: "USB", quantity: 12, packed: false },

];

function App() {
  return (
    <div className='app'>
     <Logo />
     <Form />
     <PackingList />
     <Stats />
    </div>
  )
}

function Logo(){
  return (
    <h1> Far awayüòÄüòÉ</h1>
  )
}

function Form(){
  function handleAdd(e){ //e is event
    e.preventDefault()  // to prevent reload of page from form tag

  }

   return (
    <form className='add-form' onSubmit={handleAdd}>
     <h3>Add items for your trip</h3>
     <select>
      <option value={1}>1</option>
      <option value={2}>2</option>
      <option value={3}>3</option>
      <option value={4}>4</option>
     </select>
     <input type="text" placeholder="Item..."/>
     <button onClick={handleAdd}>Add</button>
    </form>
   )
}

function PackingList(){
  return(
    <div className="list">
      <ul>
      {initialItems.map(item=>
      <Item item={item}/>)}
     </ul>
    </div>

  )
}

function Stats(){
  return (
    <footer class="stats">
      <em>You have X items in your list and you already packed X (x%)</em>  
    </footer>
  )
}

//passing item prop from parent component
function Item({item}){
  return(
    <li>
      <span style={item.packed ? {textDecoration:"line-through"}:{}}>
      {item.quantity} {item.description}
      </span>    
      <button>‚ùå</button>  
    </li>

  )

}

export default App;
